<div id="now-playing-bar-container">
   
    <div id="now-playing-bar">

        <div id="now-playing-left" class="u-playing-left-right">
           
            <div class="content">
                
                 <span class="album-link">
                     <img src="data:image/png;base64," class="album-artwork" alt="">
                 </span>

                 <div class="track-info">
                    
                     <span class="track-name">
                         <span>Happy Birthday</span>
                     </span>
                     
                     <span class="footer-artist-name">
                         <span>Arist name</span>
                     </span>
                     
                 </div>
                 
            </div>
            
        </div>

        <div id="now-playing-center">
           
            <div class="content player-controls">
               
                <div class="buttons">
                   
                    <button class="control-button shuffle" title="Suffle Button">
                        <img alt="shuffle" id="shuffle-not-active" src="assets/images/icons/shuffle.png" alt="">
                    </button>

                    <button class="control-button previous" title="Previous Button">
                        <img id="footer-previous-btn" src="assets/images/icons/previous.png" alt="">
                    </button>

                    <button class="control-button play" title="play Button">
                        <img alt="shuffle" src="assets/images/icons/play.png" class="playImg">
                    </button>

                    <button class="control-button pause" title="pause Button">
                        <img alt="shuffle" src="assets/images/icons/pause.png" class="pauseImg">
                    </button>

                    <button class="control-button next" title="next Button">
                        <img id="footer-next-btn" src="assets/images/icons/next.png" alt="">
                    </button>

                    <button class="control-button repeat" title="repeat Button">
                        <img id="repeat-not-active" alt="shuffle" src="assets/images/icons/repeat.png" alt="">
                    </button>

                </div>
                
                <div class="playback-bar">
                   
                    <span class="progress-time current">
                        0.00
                    </span>
                    
                    <div class="progress-bar">
                        <div class="progress-bar-bg">
                            <div class="progress">

                            </div>
                        </div>
                    </div>
                    
                    <span class="progress-time remaining">
                        0.00
                    </span>
                    
                </div>
                
            </div>
            
        </div>

        <div id="now-playing-right" class="u-playing-left-right">
           
            <div class="volume-bar">
               
                <button class="control-button volume" title="volume button">
                    <img src="assets/images/icons/volume.png" alt="volume">
                </button>
                
            </div>
            
        </div>

    </div>

</div>